import { useEffect, useState, useRef, useOptimistic } from 'react';

type Messages = {
    text: string,
    sending?: boolean,
    key?: number
}[]

type ParentProps = { 
    messages: Messages, 
    sendMessage: (formData: FormData) => Promise<void> 
}

const deliverMessage = async (message: string) => {
    await new Promise((response) => setTimeout(response, 1000));
    return message;
}

const UseOptimisticComponent = () => {
    const [messages, setMessages] = useState<Messages>([
        { text: `Hello!!`, sending: false, key: 1 }
    ]);

    const sendMessage = async (message: string) => {
        const sentMessage = await deliverMessage(formData.get(`message`) as string);

        setMessages((messages) => [...messages, { text: sentMessage }]);
    };

    return (
        <ParentComponent messages={messages} sendMessage={sendMessage} />
    );
};

const ParentComponent = ({ messages, sendMessage } : ParentProps) => {
    const formRef = useRef(null);
    const [optimisticState, addOptimistic] = useOptimistic<Messages, string>(
        messages, 
        (state, newMessage) => [...state, { text: newMessage, sending: true, key: state.length + 1 }]
    );

    const formAction = async (formData: FormData) => {
        addOptimistic(formData.get(`message`) as string);
        formRef.current.reset();

        await sendMessage(formData);
    }
    useEffect(() => {
        console.log(optimisticState);
    }, [optimisticState])
    return (
        <>
            {optimisticState.map((message, index) => (
                <div key={index}>
                    {message.text}
                    {!!message.sending && <small> (Sending...)</small>}
                </div>
            ))}
            <form action={formAction} ref={formRef}>
                <input type="text" name="message" />
                <button type="submit">Add Message</button>
            </form>
        </>
    )
}

export default UseOptimisticComponent;
