import React, { use, Suspense, useState } from 'react';

const fetchMyInfo = () => new Promise((resolve) => setTimeout(() => {
    return 'Hello, My name is dean.'
}, 5000));

const App = () => {
    const [myInfoPromise, setMyInfoPromise] = useState(() => fetchMyInfo());

    const handleUpdate = () => {
        fetchMyInfo().then((myInfo) => {
            setMyInfoPromise(Promise.resolve(myInfo));
        });
      };

    return (
        <>
        <div>Refresh!</div>
        <ParentComponent myInfoPromise={myInfoPromise} />
        </>
    );
};

const ParentComponent = ({ myInfoPromise }) => {
    return (
        <>
            <p>I`m Parent</p>
            <Suspense fallback={<div>Is Loading...</div>}>
                <ChildComponent myInfoPromise={myInfoPromise} />
            </Suspense>
        </>
    )
}

const ChildComponent = ({ myInfoPromise }) => {
    const myInfo = use(myInfoPromise)

    return (
        <>
            <p>I`m Child</p>
            <p>{myInfo}</p>
        </>
    )
}

export default App;
