import { useFormState } from "react-dom";

const addMessage = async (previousState: string, formData: FormData) => {
    const message = formData.get('message');
    return new Promise((resolve) => setTimeout(() => resolve(previousState + message)) );
}

const UseFormStateComponent = () => {
    const [state, formAction] = useFormState<string, FormData>(addMessage, ``);

    return (
        <>
            <p>{state}</p>
            <form action={formAction}>
                <input type="text" name="message" />
                <button type="submit">Add Message</button>
            </form>
        </>
    );
}

export default UseFormStateComponent;
